<?phpclass HightFrecencyCryptoTrading {    CONST CONF = 'all.json';    public static $resources;    public static $histories;    public static $params;    public static $response;    public function __construct()    {        $input = json_decode(file_get_contents(self::CONF));        self::$params = $input->params;        self::$resources = $input->resources;        self::$histories = $input->histories;        self::$response = $input->response;    }}HightFrecencyCryptoTrading();